// src/components/RearrangeSentence.js

import React, { useState } from 'react';
import pinyin from 'pinyin'; // Pinyinライブラリをインポート

const RearrangeSentence = ({ group }) => {
    const [selectedWords, setSelectedWords] = useState([]);
    const [isConfirmed, setIsConfirmed] = useState(false);
    const [resultSymbol, setResultSymbol] = useState(null);
    const [currentProblemIndex, setCurrentProblemIndex] = useState(0);
    const [shuffledWords, setShuffledWords] = useState([]);

    const currentProblem = group.problems[currentProblemIndex];

    // ランダムに単語ボタンを生成（初回のみ）
    const getRandomizedWords = () => {
        if (shuffledWords.length === 0) {
            const allWords = [...currentProblem.correctOrder]; // 正解の単語
            const shuffled = allWords.sort(() => Math.random() - 0.5); // ランダム化
            setShuffledWords(shuffled); // シャッフルした単語を保存
        }
        return shuffledWords.length > 0 ? shuffledWords : currentProblem.correctOrder;
    };

    const toggleWordSelection = (word) => {
        setSelectedWords(prev => {
            if (prev.includes(word)) {
                return prev.filter(w => w !== word); // タップした場合は選択解除
            } else {
                return [...prev, word]; // タップした場合は選択追加
            }
        });
        // 中国語で読み上げ
        const utterance = new SpeechSynthesisUtterance(word);
        utterance.lang = 'zh-CN'; // 中国語を設定
        window.speechSynthesis.speak(utterance); // 読み上げ
    };

    const checkAnswer = () => {
        if (selectedWords.join(',') === currentProblem.correctOrder.join(',')) {
            setResultSymbol('⚪︎'); // 正解のシンボル
        } else {
            setResultSymbol('×'); // 不正解のシンボル
        }
        setIsConfirmed(true);
    };

    const nextProblem = () => {
        if (currentProblemIndex < group.problems.length - 1) {
            setCurrentProblemIndex(prev => prev + 1);
            setSelectedWords([]);
            setIsConfirmed(false);
            setResultSymbol(null);
            setShuffledWords([]); // 次の問題に進んだらシャッフルをリセット
        } else {
            // 最後の問題が終わった場合、結果を表示
            alert("全ての問題が終了しました！"); // 結果を表示
        }
    };

    // Pinyinを生成する関数
    const generatePinyin = (words) => {
        return pinyin(words.join(''), { style: pinyin.STYLE_TONE }); // Pinyinを生成
    };

    return (
        <div>
            <h2>{currentProblem.translation}</h2>
            <div>
                {getRandomizedWords().map((word, index) => (
                    <div key={index} style={{ display: 'inline-block', margin: '5px' }}>
                        <button
                            onClick={() => toggleWordSelection(word)}
                            style={{
                                backgroundColor: selectedWords.includes(word) ? 'lightgreen' : 'lightblue' // 選択時の色変更
                            }}
                        >
                            {word}
                        </button>
                        <div style={{ color: 'gray', fontSize: '0.8em', textAlign: 'center' }}>
                            {generatePinyin([word])[0]} {/* Pinyinを表示 */}
                        </div>
                    </div>
                ))}
            </div>
            <div>
                <h3>選択された単語: {selectedWords.join(' ')}</h3>
            </div>
            <button onClick={checkAnswer}>確定</button>
            {isConfirmed && (
                <div>
                    <p>{resultSymbol}</p>
                    <button onClick={nextProblem}>次へ</button>
                </div>
            )}
        </div>
    );
};

export default RearrangeSentence;
