import React from 'react';
import pinyin from 'pinyin';

const RearrangeSentence = ({ group }) => {
    const renderSentenceWithPinyin = (sentence) => {
        const pinyinList = pinyin(sentence.correctOrder.join(''), { style: pinyin.STYLE_TONE });

        return sentence.correctOrder.map((word, index) => (
            <div key={index} style={{ position: 'relative', display: 'inline-block' }}>
                <span style={{ color: 'gray', position: 'absolute', top: '-20px', left: '0' }}>
                    {pinyinList[index].join(' ')} {/* 自動生成されたPinyinを表示 */}
                </span>
                <span onClick={() => handleWordClick(word)}>
                    {word}
                </span>
            </div>
        ));
    };

    const handleWordClick = (word) => {
        const utterance = new SpeechSynthesisUtterance(word);
        utterance.lang = 'zh-CN'; // 中国語を指定
        window.speechSynthesis.speak(utterance);
    };

    return (
        <div>
            <h2>{group.name}</h2>
            {group.problems.map((problem, index) => (
                <div key={index}>
                    {renderSentenceWithPinyin(problem)}
                </div>
            ))}
        </div>
    );
};

export default RearrangeSentence;
