import pinyin from 'pinyin';

// Pinyinを生成する関数
const generatePinyin = (characters) => {
    return pinyin(characters.join(''), { style: pinyin.STYLE_TONE });
};

export const problemGroups = [
    {
        name: 'HSK1',
        problems: [
            {
                correctOrder: ["爸爸", "今天", "不", "在", "家"],
                translation: "お父さんは今日家にいません",
                pinyin: generatePinyin(["爸爸", "今天", "不", "在", "家"]) // 自動的にPinyinを生成
            },
            {
                correctOrder: ["妈妈", "在", "上班"],
                translation: "お母さんは仕事にいっています",
                pinyin: generatePinyin(["妈妈", "在", "上班"]) // 自動的にPinyinを生成
            },
            {
                correctOrder: ["你", "儿子", "今年", "几", "岁", "了", "？"],
                translation: "あなたの息子は今年何歳ですか？",
                pinyin: generatePinyin(["你", "儿子", "今年", "几", "岁", "了", "？"]) // 自動的にPinyinを生成
            },
            // 他の問題を追加...
        ],
    },
    {
        name: 'HSK2',
        problems: [
            {
                correctOrder: ["我", "女儿", "在", "北京", "上", "大学"],
                translation: "私の娘は北京の大学に通っています",
                pinyin: generatePinyin(["我", "女儿", "在", "北京", "上", "大学"]) // 自動的にPinyinを生成
            },
            // 他の問題を追加...
        ],
    },
    // 他のグループを追加...
];
